CC++ <pre><code>/** By: Negin Eini-pour **/
#include &lt;stdio.h&gt;

int main() {
    int n;
    scanf("%d", &amp;n);
    int arr[15];
    for (int i = 0; i &lt; n; i++) {
        scanf("%d", &amp;arr[i]);
    }

    int max_length = 0;
    int start = -1;
    int end = -1;

    for (int i = 0; i &lt; n; i++) {
        int sum = 0;
        for (int j = i; j &lt; n; j++) {
            sum += arr[j];
            if (sum == 0) {
                int current_length = j - i + 1;
                if (current_length &gt; max_length) {
                    max_length = current_length;
                    start = i;
                    end = j;
                }
            }
        }
    }

    if (max_length == 0) {
        printf("[]\n");
    } else {
        printf("[");
        for (int i = start; i &lt;= end; i++) {
            printf("%d", arr[i]);
            if (i != end) {
                printf(", ");
            }
        }
        printf("]\n");
    }

    return 0;
}
